try {
  // Get current metrics or parse them
  let currentMetrics;
  if ($json.stdout) {
    // Parse command output
    currentMetrics = JSON.parse($json.stdout);
  } else if ($json.timestamp) {
    // Metrics are already parsed
    currentMetrics = $json;
  } else {
    throw new Error('Invalid metrics data');
  }

  // Get history from workflow variable
  let history = [];

  try {
    if ($workflow && $workflow.variables && $workflow.variables.statsHistory) {
      if (typeof $workflow.variables.statsHistory === 'string') {
        try {
          history = JSON.parse($workflow.variables.statsHistory);
        } catch (parseErr) {
          console.log('Error parsing history string:', parseErr.message);
          history = [];
        }
      } else if ($workflow.variables.statsHistory.value) {
        history = $workflow.variables.statsHistory.value;
      }
    }
  } catch (e) {
    console.log('Error accessing history variable:', e.message);
  }

  // Ensure history is an array
  if (!Array.isArray(history)) {
    history = [];
  }

  // Extract the important metrics for time series
  const timeSeriesData = {
    timestamp: currentMetrics.timestamp,
    cpu: {
      load: currentMetrics.cpu?.load || 0,
      temperature: currentMetrics.cpu?.temperature || 'N/A'
    },
    memory: {
      usage: currentMetrics.memory?.usage || 0
    },
    disk: {
      usage: currentMetrics.disk?.usage || 0
    },
    network: {
      bytesIn: currentMetrics.network?.bytesIn || 0,
      bytesOut: currentMetrics.network?.bytesOut || 0
    },
    system: {
      loadAverage: currentMetrics.system?.loadAverage || { '1min': 0, '5min': 0, '15min': 0 }
    }
  };

  // Add new data point to history
  history.push(timeSeriesData);

  // Keep only the last 144 data points (10 min intervals = 24 hours of data)
  // You can increase this to store more history, but mind the variable size limit
  const maxDataPoints = 144;
  if (history.length > maxDataPoints) {
    history = history.slice(history.length - maxDataPoints);
  }

  // Store updated history in workflow variable
  try {
    if ($workflow && $workflow.variables) {
      $workflow.variables.statsHistory = JSON.stringify(history);
    } else {
      console.log('Warning: Could not set statsHistory, $workflow.variables is not available');
    }
  } catch (varErr) {
    console.log('Error setting workflow variable:', varErr.message);
  }

  // Return both current data and historical data
  return [{
    json: {
      current: currentMetrics,
      history: history,
      timestamp: new Date().toISOString(),
      historyCount: history.length
    }
  }];
} catch (error) {
  return [{
    json: {
      error: true,
      message: error.message,
      timestamp: new Date().toISOString()
    }
  }];
}

