try {
  // Get the metrics data
  let m;

  // If we have stdout from the command, parse it
  if ($json.stdout) {
    try {
      m = JSON.parse($json.stdout);
    } catch (e) {
      throw new Error(`Failed to parse command output: ${e.message}`);
    }
  } else {
    m = $json;
  }

  // Add a defensive check to ensure m is an object
  if (!m || typeof m !== 'object') {
    throw new Error('Invalid metrics data');
  }

  // Check if there was an error during command execution
  if (m.error) {
    throw new Error(`Error collecting metrics: ${m.message}`);
  }

  // Define warning thresholds
  const thresholds = { 
    cpu: 0.8, 
    mem: 0.85, 
    disk: 0.9 
  };

  // Calculate health status
  m.health = { 
    status: 'healthy', 
    warnings: [],
    statusCode: 0 // 0=healthy, 1=warning, 2=critical
  };

  // Check CPU usage
  if (m.cpu && m.cpu.load > thresholds.cpu) {
    m.health.status = 'warning';
    m.health.statusCode = 1;
    m.health.warnings.push('High CPU usage');
  }

  // Check memory usage
  if (m.memory && m.memory.usage > thresholds.mem) {
    m.health.status = 'warning';
    m.health.statusCode = 1;
    m.health.warnings.push('High memory usage');
  }

  // Check disk usage
  if (m.disk && m.disk.usage > thresholds.disk) {
    m.health.status = 'warning';
    m.health.statusCode = 1;
    m.health.warnings.push('High disk usage');
  }

  // Format display values for human readability
  m.display = {
    memory: {
      total: m.memory ? `${(m.memory.total / (1024 ** 3)).toFixed(2)} GB` : 'N/A',
      used: m.memory ? `${(m.memory.used / (1024 ** 3)).toFixed(2)} GB` : 'N/A',
      available: m.memory ? `${(m.memory.free / (1024 ** 3)).toFixed(2)} GB` : 'N/A',
      usage: m.memory ? `${(m.memory.usage * 100).toFixed(2)}%` : 'N/A'
    },
    disk: {
      total: m.disk ? `${(m.disk.total / (1024 ** 3)).toFixed(2)} GB` : 'N/A',
      used: m.disk ? `${(m.disk.used / (1024 ** 3)).toFixed(2)} GB` : 'N/A',
      available: m.disk ? `${(m.disk.available / (1024 ** 3)).toFixed(2)} GB` : 'N/A',
      usage: m.disk ? `${(m.disk.usage * 100).toFixed(2)}%` : 'N/A'
    },
    cpu: {
      load: m.cpu ? `${(m.cpu.load * 100).toFixed(2)}%` : 'N/A',
      cores: m.cpu ? m.cpu.cores : 'N/A',
      temperature: m.cpu ? m.cpu.temperature : 'N/A'
    },
    system: {
      uptimeString: m.system ? (() => {
        const up = m.system.uptime;
        const d = Math.floor(up / 86400);
        const h = Math.floor((up % 86400) / 3600);
        const min = Math.floor((up % 3600) / 60);
        const s = Math.floor(up % 60);
        return `${d}d ${h}h ${min}m ${s}s`;
      })() : 'N/A'
    },
    network: {
      bytesIn: m.network ? `${(m.network.bytesIn / 1024).toFixed(2)} KB/s` : 'N/A',
      bytesOut: m.network ? `${(m.network.bytesOut / 1024).toFixed(2)} KB/s` : 'N/A',
      packetLoss: m.network ? `${(m.network.packetLoss * 100).toFixed(2)}%` : 'N/A'
    }
  };

  // Add metadata
  m.meta = {
    generated: new Date().toISOString(),
    source: 'n8n-server-stats-webhook',
    version: '1.0.0'
  };

  // Safely set the workflow variable
  try {
    if ($workflow && $workflow.variables) {
      const cacheData = { ...m, timestamp: new Date().toISOString() };
      // Store as a properly formatted JSON string
      $workflow.variables.statsCache = JSON.stringify(cacheData);
    } else {
      console.log('Warning: Could not set statsCache, $workflow.variables is not available');
    }
  } catch (varErr) {
    console.log('Error setting workflow variable:', varErr.message);
  }

  // Return the complete, processed metrics
  return [{ json: m }];
} catch (err) {
  // Handle any errors and return error data
  return [{ 
    json: { 
      error: true, 
      message: err.message, 
      timestamp: new Date().toISOString(),
      stackTrace: err.stack
    }
  }];
}

