node -e '
  const os   = require("os");
  const fs   = require("fs");
  const exec = require("child_process").execSync;

  // 1) Inizializzo metrics con tutti i gruppi
  const metrics = {
    timestamp: new Date().toISOString(),
    system:   {},
    cpu:      {},
    memory:   {},
    disk:     {},
    network:  {}
  };

  // 2) Popolo system
  metrics.system.hostname    = os.hostname();
  metrics.system.os          = `${os.type()} ${os.release()}`;
  metrics.system.uptime      = os.uptime();
  const [l1,l5,l15]          = os.loadavg();
  metrics.system.loadAverage = { "1min": l1, "5min": l5, "15min": l15 };

  // 3) Popolo cpu
  metrics.cpu.cores = os.cpus().length;
  {
    let totalIdle = 0, totalTick = 0;
    os.cpus().forEach(cpu => {
      for (const t in cpu.times) totalTick += cpu.times[t];
      totalIdle += cpu.times.idle;
    });
    metrics.cpu.load = 1 - totalIdle/totalTick;
  }
  try {
    const t = exec("cat /sys/class/thermal/thermal_zone0/temp")
                .toString().trim();
    metrics.cpu.temperature = `${(parseInt(t,10)/1000).toFixed(1)}Â°C`;
  } catch { metrics.cpu.temperature = "N/A"; }

  // 4) Popolo memory
  metrics.memory.total = os.totalmem();
  metrics.memory.free  = os.freemem();
  metrics.memory.used  = metrics.memory.total - metrics.memory.free;
  metrics.memory.usage = metrics.memory.used/metrics.memory.total;

  // 5) Popolo disk
  try {
    const parts = exec("df -k / | tail -1").toString().trim().split(/\s+/);
    metrics.disk.total     = parseInt(parts[1],10)*1024;
    metrics.disk.used      = parseInt(parts[2],10)*1024;
    metrics.disk.available = parseInt(parts[3],10)*1024;
    metrics.disk.usage     = metrics.disk.used/metrics.disk.total;
  } catch {
    metrics.disk = { total:0, used:0, available:0, usage:0 };
  }

  // 6) Popolo network
  try {
    metrics.network.connections =
      parseInt(exec("netstat -an | grep ESTABLISHED | wc -l").toString(),10);
    const dev = fs.readFileSync("/proc/net/dev","utf8")
                  .split("\n").slice(2);
    let inB=0, outB=0;
    dev.forEach(l => {
      const cols = l.trim().split(/\s+/);
      if (cols.length>=10) {
        inB  += parseInt(cols[1],10);
        outB += parseInt(cols[9],10);
      }
    });
    metrics.network.bytesIn  = inB;
    metrics.network.bytesOut = outB;
    try {
      const ping = exec("ping -c 5 -q 8.8.8.8").toString();
      const m = ping.match(/(\d+)% packet loss/);
      metrics.network.packetLoss = m ? parseInt(m[1],10)/100 : 0;
    } catch { metrics.network.packetLoss = 0; }
  } catch {
    metrics.network = { connections:0, bytesIn:0, bytesOut:0, packetLoss:0 };
  }

  // Stampo il JSON formattato
  console.log(JSON.stringify(metrics, null, 2));
'

