// Check if we should use cached data
let cache;
try {
  // Safely try to access the cache variable
  if ($workflow && $workflow.variables && $workflow.variables.statsCache) {
    // Handle both string and object formats
    if (typeof $workflow.variables.statsCache === 'string') {
      try {
        cache = JSON.parse($workflow.variables.statsCache);
      } catch (parseErr) {
        console.log('Error parsing cache string:', parseErr.message);
        cache = null;
      }
    } else if ($workflow.variables.statsCache.value) {
      cache = $workflow.variables.statsCache.value;
    }
  }
} catch (e) {
  console.log('Error accessing cache:', e.message);
  cache = null;
}

// Check if cache is valid and not expired
const now = new Date();
if (cache && cache.timestamp) {
  try {
    const cacheTime = new Date(cache.timestamp);
    const diffSec = (now - cacheTime) / 1000;
    if (diffSec < 30) {
      // Cache is valid, use it
      return [{ json: { ...cache, fromCache: true } }];
    }
  } catch (e) {
    console.log('Error processing cache timestamp:', e.message);
  }
}

// Cache is invalid or expired, request new data
return [{ json: { needsUpdate: true } }];

